(function(){"use strict";angular.module("app",["ngCookies","angular-jwt"]);angular.module("app").controller("LoginController",["$scope","$http","$window","$cookies","jwtHelper",function($scope,$http,$window,$cookies,jwtHelper){$scope.authFailed=!1;$scope.login=function(){return $http.post("/authenticate",{email:$scope.email,password:$scope.password}).success(function(data,status,headers,config){var expDate;expDate=jwtHelper.getTokenExpirationDate(data.token);$cookies.put("token",data.token,{expires:expDate});return $window.location.href="/game"}).error(function(data,status,headers,config){return $scope.authFailed=!0})}}]);})();